class Solution {

    int solve(vector<int>& nums, int i, int prev1, int prev2, vector<vector<vector<int>>> &dp )
    {
        if(i>=nums.size())
        {
            return 1;
        }
        if(dp[i][prev1][prev2]!=-1)
        {
            return dp[i][prev1][prev2];
        }
        int ans=0;
        int cur=nums[i];
        int ct=0;
        for(int j=0;j<=cur;j++)
        {
            if(j>=prev1&&(cur-j)<=prev2)
            {
                // ct++;
                // ans++;
                // cout<<"SGSG";
                ans=(ans+solve(nums, i+1, j, cur-j, dp))%1000000007;
            }
        }
        return dp[i][prev1][prev2] =ans;
    }


public:
    int countOfPairs(vector<int>& nums) {
        // int ans=0;
        int n=nums.size();
         vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
    int ans=   solve(nums, 0, 0, 50, dp);
        return ans;
    }

};
