class Solution {
public:
    long long mod=1000000007;
    long long totalArrays(int curId,int prevNumber,vector<int> &nums,vector<vector<long long>> &dp){

        if(nums.size()==curId){
            return 1;
        }

        if(prevNumber!=-1 && dp[curId][prevNumber]!=-1){
            return dp[curId][prevNumber];
        }
        int curNumber=nums[curId];
        int ans=0;

        for(int num=0;num<=curNumber;num++){


            if(prevNumber==-1 || (prevNumber<=num && nums[curId-1]-prevNumber >=nums[curId]-num)){
                int next=totalArrays(curId+1,num,nums,dp);
                ans=(ans%mod+next%mod)%mod;
            }
        }
        if(prevNumber!=-1){
            dp[curId][prevNumber]=ans;
        }
        return ans;
    }
    int countOfPairs(vector<int>& nums) {
        int prevNumber=-1;
        int total=nums.size();
        vector<vector<long long>> dp(total,vector<long long>(52,-1));
        long long ans=totalArrays(0,prevNumber,nums,dp);
        return ans;
    }
};
