class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10 ** 9 + 7

        @lru_cache(None)
        def dfs(i, prev1, prev2):
            if i == len(nums):
                return 1
            res = 0
            for cur1 in range(nums[i], prev1 - 1, -1):
                cur2 = nums[i] - cur1
                if cur2 > prev2:
                    break
                res += dfs(i+1, cur1, cur2)

            return res % MOD

        return dfs(0, 0, nums[0])
