class Solution {
public:
    long long dp[2001][51][51];
    const long long MOD=1e9 +7;
    long long sol(int idx,vector<int>& values,int smallest,int largest){
        int n=values.size();
        if(idx>=n){
            return 1;
        }
        if(dp[idx][smallest][largest]!=-1){
            return dp[idx][smallest][largest];
        }
        long long ans = 0;
        for(int p=smallest;p<values[idx]+1;p++){
            // int x=values[idx]-p;
            for(int q=largest;q>=0;q--){
                int y=values[idx]-p;
                // int q=values[idx]-p;
                if(q==y){
                    ans+=sol(idx+1,values,p,q);
                    ans%=MOD;
                }
            }
        }

        return dp[idx][smallest][largest]=ans;
    }

    int countOfPairs(vector<int>& values) {
        memset(dp,-1,sizeof(dp));
        int initialx=0,initialy=values[0];
        return sol(0,values,initialx,initialy);
    }
};
