#define mod 1000000007
class Solution {
public:
    int help(vector<int>& nums,const int& n,int ind,int incprev,int decprev,map<pair<int,long long>,long long>& ma){
        if(ind==n)
                return 1;
        if(ma.find({ind,incprev})!=ma.end())
                return ma[{ind,incprev}];
        long long ans=0;
        int inc=incprev;
        while(inc<=nums[ind]){
            int dec=nums[ind]-inc;
            if(inc>=incprev && dec<=decprev && dec>=0){
                ans+=help(nums,n,ind+1,inc,dec,ma);
                ans%=mod;
            }
            inc++;
        }
        return ma[{ind,incprev}]=ans;

    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        map<pair<int,long long>,long long> ma;
        return help(nums,n,0,0,INT_MAX,ma);
    }
};
