class Solution {
public:

    int mod = 1e9 + 7;
    int t[2001][51][52];
    long long solve(vector<int>& nums, int i, int prevA, int prevB) {
        if(i >= nums.size()) {
            return 1;
        }
        if(prevB == INT_MAX) {
            if(t[i][prevA][51] != -1) return t[i][prevA][51];
        } else {
            if(t[i][prevA][prevB] != -1) return t[i][prevA][prevB];
        }

        int num = nums[i]; // 0---num
        long long res = 0;
        for(int k = 0; k <= num; k++) {
            if(k >= prevA && (num-k) <= prevB) {
                res = (res + solve(nums, i+1, k, num-k))%mod;
            }
        }
        if(prevB == INT_MAX) {
            return t[i][prevA][51] = res%mod;
        } 
        return t[i][prevA][prevB] = res%mod;

    }

    int countOfPairs(vector<int>& nums) {
        memset(t, -1, sizeof(t));
        return solve(nums, 0, 0, INT_MAX);
    }
};
