#define ll long long int
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<ll>>> dp(n, vector<vector<ll>>(51, vector<ll>(51, -1)));

        function<ll(int, int, int)> countPairs = [&](int idx, int prev1, int prev2) -> ll {
            if (idx >= n) return 1;
            ll result = 0;
            ll mod = 1e9 + 7;
            if (dp[idx][prev1][prev2] != -1) return dp[idx][prev1][prev2];

            for (int a = prev1; a <= nums[idx]; ++a) {
                for (int b = prev2; b >= 0; --b) {
                    if (a + b == nums[idx]) {
                        result += countPairs(idx + 1, a, b);
                        result %= mod;
                    }
                }
            }
            return dp[idx][prev1][prev2] = result;
        };

        return countPairs(0, 0, nums[0]);
    }
};
