class Solution {
public:
    long long dp[2001][54];
    long long int cal(int i, int l, int r,vector<int>& nums){
        if(i>=nums.size())return 1;
        if(dp[i][r]!=-1)return dp[i][r];
        long long int sum=0;
        for(int k=l;k<=nums[i];k++){
            int nr=nums[i]-k;
            if(nr<=r){
                sum=(sum%1000000007 + cal(i+1, k, nr,nums)%1000000007)%1000000007;
             }
        }
       // cout<<i<<" "<<l<<" "<<r<<" "<<sum<<endl;
        return dp[i][r]=sum;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
       return cal(0, 0, 51,nums); 
    }
};
