class Solution {
    int mod = ((int)1e9) + 7;

    public int countOfPairs(int[] nums) {
        int prev1 = 0, prev2 = 100;
        int n = nums.length;
        int[][] dp = new int[n][51];

        for(int[] k : dp){

                Arrays.fill(k,-1);

        }


        return (helper(nums,0,prev1,dp))%mod;
    }

    private int helper(int[] nums,int index,int prev1,int[][] dp){
        if(index == nums.length){
            return 1;
        }

        if(dp[index][prev1]!=-1){
            return dp[index][prev1];
        }

        if(prev1 > nums[index])return dp[index][prev1] = 0;


        int ans = 0;

        int prev2 = index>0 ? nums[index-1]-prev1 : 100;

        for(int next1 = prev1; next1 <= nums[index] ; next1++){
            int next2 = nums[index] - next1;
            if(next2 > prev2){
                continue;
            }
            ans = (ans + (helper(nums,index+1, next1, dp)%mod))%mod;

        }

        return dp[index][prev1] = (ans%mod);
    }
}
