#define MOD 1000000007
class Solution {
public:
    int f(vector<int>& nums,int i,int val,vector<vector<int>>& dp)
    {
        if(i==nums.size())
        return 1;
        if(dp[i][val]!=-1)
        return dp[i][val];
        int ans=0,lessthan;
        if(i==0)
        lessthan=1e5;
        else
        lessthan=nums[i-1]-val;
        for(int x=val;x<=nums[i];x++)
        {
            if(nums[i]-x<=lessthan)
            ans=(ans+f(nums,i+1,x,dp))%MOD;
        }
        return dp[i][val]=ans%MOD;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int>> dp(n,vector<int>(51,-1));
        return f(nums,0,0,dp);
    }
};
