class Solution {
public:
const int mod = 1e9 + 7;

int solve(int i, int n, vector<int>& nums, int prev1, int prev2, vector<vector<vector<int>>>& dp) {
    if (i == n) return 1;
    if (dp[i][prev1][prev2] != -1) return dp[i][prev1][prev2];

    int cnt = 0;
    for (int j = 0; j <= nums[i]; j++) {
        int l = j, r = nums[i] - j;
        if (l >= prev1 && r <= prev2)
            cnt = (cnt + solve(i + 1, n, nums, l, r, dp)) % mod;
    }

    return dp[i][prev1][prev2] = cnt;
}

int countOfPairs(vector<int>& nums) {
    int n = nums.size();
    int maxNum = *max_element(nums.begin(), nums.end());
    vector<vector<vector<int>>> dp(n, vector<vector<int>>(maxNum + 1, vector<int>(maxNum + 1, -1)));

    return solve(0, n, nums, 0, maxNum, dp);
}
};
