class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7

        # Memoization dictionary
        memo = {}

        def backtrack(index, prev1, prev2):
            if index == len(nums):
                return 1

            if (index, prev1, prev2) in memo:
                return memo[(index, prev1, prev2)]

            count = 0
            for x in range(prev1, nums[index] + 1):
                y = nums[index] - x
                if y <= prev2:
                    count += backtrack(index + 1, x, y)
                    count %= MOD

            memo[(index, prev1, prev2)] = count
            return count

        return backtrack(0, 0, nums[0])
