class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>>prev,cur;
        int ans=0,m=1e9+7,n=nums.size();
        prev.push_back({INT_MAX,INT_MIN,1});
        for(int i=0;i<n;i++) {
            for(int x=0;x<=nums[i];x++) {
                int val=0;
                for(int j=0;j<prev.size();j++) {
                    if(x<=prev[j][0] && nums[i]-x>=prev[j][1]) {
                        val=(1LL*val+prev[j][2])%m;
                    }
                }
                if(val) cur.push_back({x,nums[i]-x,val});
            }
            prev=cur;
            cur.clear();
        }
        for(auto &i:prev) ans=(1LL*ans+i[2])%m;
        return ans;
    }
};
