typedef long long ll;
class Solution {
public:
    const int mod = 1e9+7;
    int countOfPairs(vector<int>& nums) {
        vector<vector<ll>> DP(nums.size(), vector<ll>(1001));
        fill(DP[0].begin(), DP[0].begin()+nums[0]+1, 1);
        for(int i = 1; i <= 1000; ++i) DP[0][i] += DP[0][i-1];
        for (int i = 1; i < nums.size(); ++i) {
            for(int j = 0; j <= 1000; ++j) {
                // k >= j
                // nums[i-1]+j-nums[i] <= k
                // 
                if (j <= nums[i] && nums[i-1]+j-nums[i] <= nums[i-1]) {
                    int llim = max(j, nums[i-1]+j-nums[i]);
                    DP[i][j] = DP[i-1][1000]-(llim > 0 ? DP[i-1][llim-1] : 0)+mod;
                    DP[i][j] %= mod;
                }
                if (j) DP[i][j] += DP[i][j-1];
                DP[i][j] %= mod;
            }
        }
        return DP[nums.size()-1][nums.back()];
    }
};
