class Solution {
public:

    int MOD=1e9+7;
    int dp[2005][51];
    int n;

    // int rec(int i, int last_a, vector<int>& nums){
    //     // int last_b = nums[i]-last_a;
    //     // if (last_b < 0 || last_a > n)

    //     if (last_a > nums[i]) return 0;

    //     if (dp[i][last_a] != -1) return dp[i][last_a];

    // }

    int rec(int i, int last_a, vector<int>& nums){

        if (i == n-1){
            if (last_a <= nums[i]) return 1;
            return 0;
        }

        if (last_a > nums[i]) return 0;

        int last_b = nums[i]-last_a;

        if (dp[i][last_a] != -1) return dp[i][last_a];
        int ans=0;
        for (int j=last_a; j <= nums[i+1]; j++){
            int last_b_next = nums[i+1]-j;
            if (last_b_next <= last_b){
                ans = (ans + rec(i+1, j, nums))%MOD;
            }
        }
        return dp[i][last_a] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        n = nums.size();

            memset(dp, -1, sizeof(dp));

        int f=0;
        for (int i=0; i <= nums[0]; i++){
            f = (f + rec(0, i, nums))%MOD;
        }
        // cout<<f<<endl;
        return f;
    }
};
