class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9+7
        @cache
        def dfs(i,prev):
            if i == len(nums):
                return 1
            s = 0
            for cur in range(nums[i]+1):
                if prev == -1 or (cur >= prev and nums[i]-cur <= nums[i-1]-prev):
                    s += dfs(i+1,cur)
                    s %= mod
            return s
        return dfs(0,-1)
