class Solution {
public:
    int mod = 1e9+7;

    int solve(const vector<int>&nums,vector<vector<vector<int>>>& freq, int i, int aVal, int bVal){
        if(i==nums.size())return 1;
        if(freq[i][aVal][bVal]!=INT_MAX){
            return freq[i][aVal][bVal];
        }
        int count =0;
        for(int j=aVal;j<=nums[i];j++){
            int aTemp=j;
            int bTemp=nums[i]-aTemp;
            if(bTemp>bVal)continue;
            count=(count+solve(nums,freq,i+1,aTemp,bTemp))%mod;
        }
        // cout<<i<<" "<<aVal<<" "<<bVal<<endl;
        freq[i][aVal][bVal]=count;
        return count;
    }

    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<int>>> freq(nums.size(),vector<vector<int>>(51,vector<int>(51,INT_MAX)));

        long long ans=0;

        ans=(ans+solve(nums,freq,0,0,50))%mod;


        // for(int i=0;i<nums.size();i++){
        //     for(int j=0;j<5;j++){
        //         for(int k=0;k<5;k++){
        //             if(freq[i][j][k]!=INT_MAX)cout<<freq[i][j][k]<<" ";
        //             else cout<<-1<<" ";
        //         }cout<<endl;
        //     }
        //     cout<<endl;
        // }
        return ans;
    }
};
