class Solution {
public:
    int cp(vector<int> &num,int n,int p1, int p2 ,vector<vector<vector<int>>> &dp){
        if(n==-1)return 1;
        int mod=1e9+7;
        if(dp[n][p1][p2]!=-1)return dp[n][p1][p2];
        long long ans=0;
        for(int i=0;i<=num[n];i++){
            if(i>p1)break;
            if(num[n]-i<p2)break;
            ans+=cp(num,n-1,i,num[n]-i,dp)%mod;
        }
        return dp[n][p1][p2]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int mod=1e9+7;
        vector<vector<vector<int>>> dp(n,vector<vector<int>>(52,vector<int>(52,-1)));
        return cp(nums,n-1,51,0,dp)%mod;
    }
};
