const int mod = 1e9 + 7;
class Solution {
public:


	int func(int ind, int pre1, int pre2, vector<int>&v, int n, vector<vector<vector<int>>>&dp) {
		if (ind == n) return 1;
		if (dp[ind][pre1][pre2] != -1) return dp[ind][pre1][pre2];
		int count = 0;
		for (int j = pre1; j < v[ind] + 1; ++j)
		{
			/* code */int pre3 = v[ind] - j;
			if (pre3 <= pre2) {
				count = (count + func(ind + 1, j, pre3, v, n, dp)) % mod;


			}
		}
		return dp[ind][pre1][pre2] = count;
	}

	int countOfPairs(vector<int>&v) {
		int n = v.size();
		vector<vector<vector<int>>> dp(n + 1, vector<vector<int>>(55, vector<int>(55, -1)));
		return func(0, 0, 50, v, n, dp);
	}
};
