#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    vector<vector<vector<long long int>>> dp;
    int n;
    long long int mod = 1e9 + 7;

    long long int memo(int ind, int val1, int val2, vector<int>& vals) {
        if (ind >= n) return 1;
        if (dp[ind][val1][val2] != -1) return dp[ind][val1][val2];

        long long int result = 0;
        for (int i = val1; i <= vals[ind]; ++i) {
            for (int j = val2; j >= 0; --j) {
                if (i + j == vals[ind]) {
                    result += memo(ind + 1, i, j, vals);
                    result %= mod;
                }
            }
        }
        return dp[ind][val1][val2] = result;
    }

    int countOfPairs(vector<int>& vals) {
        n = vals.size();
        dp = vector<vector<vector<long long int>>>(n, vector<vector<long long int>>(51, vector<long long int>(51, -1)));
        return memo(0, 0, vals[0], vals);
    }
};
