class Solution {
public:
    int MOD = 1e9 + 7;

    int rec(vector<int>& nums, vector<vector<vector<int>>>& dp, int i, int last, int revlast) {
        if (i == nums.size()) {
            return 1;
        }
        if (dp[i][last][revlast] != -1) {
            return dp[i][last][revlast];
        }

        int ans = 0;
        for (int x = last; x <= nums[i]; ++x) {
            if (revlast >= nums[i] - x) {
                ans = (ans + rec(nums, dp, i + 1, x, nums[i] - x)) % MOD;
            }
        }
        return dp[i][last][revlast] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        // Initialize DP table with -1 (indicating uncomputed state)
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return rec(nums, dp, 0, 0, 50);
    }
};
