class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();


        vector<vector<int>> dp(n, vector<int>(51, 0));


        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1; // arr1[0] = j, arr2[0] = nums[0] - j
        }


        for (int i = 1; i < n; ++i) {
            for (int j = 0; j <= nums[i]; ++j) {
                for (int k = 0; k <= j; ++k) { 
                    int arr2i = nums[i] - j;
                    int arr2i_1 = nums[i-1] - k;
                    if (arr2i <= arr2i_1) { 
                        dp[i][j] = (dp[i][j] + dp[i-1][k]) % MOD;
                    }
                }
            }
        }


        int result = 0;
        for (int j = 0; j <= 50; ++j) {
            result = (result + dp[n-1][j]) % MOD;
        }

        return result;
    }
};
