class Solution {
public:
    int dp[2001][51][51];
    int M=1e9+7;
    int solve(vector<int>&nums, int ind, int la, int lb){
        if(ind==nums.size()){
            return 1;
        }
        if(dp[ind][la][lb]!=-1) return dp[ind][la][lb];
        int st=la;
        long long ans=0;
        for(int i=st;i<=nums[ind];i++){
            int cura=i;
            int curb=nums[ind]-i;
            if(ind==0 || lb>=curb){
                ans=(ans+solve(nums, ind+1, cura, curb))%M;
            }
        }
        return dp[ind][la][lb]=ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        memset(dp, -1, sizeof(dp));
        return solve(nums, 0, 0, 0);
    }
};
