#define mod 1000000007

class Solution {
public:
    int n;
    long long int dp[2001][51][51];
    long long int solve(vector<int>& nums, int i, int mn, int mx){
        if(i == n-1){
            int cnt = 0;
            for(int j = mn;j<=nums[i];j++){
                int k = nums[i] - j;
                if(k <= mx){
                    cnt++;
                }
            }
            return (dp[i][mn][mx] = (cnt%mod));

        }
        if(dp[i][mn][mx]!=-1){
            return (dp[i][mn][mx]%mod);
        }

        long long int cnt = 0;
         for(int j = mn;j<=nums[i];j++){
            int k = nums[i] - j;
            if(k <= mx){
                cnt+= solve(nums, i+1, j, k)%mod;
            }
        }
        return (dp[i][mn][mx] = (cnt%mod));
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        n = nums.size();
        return solve(nums, 0, 0, nums[0])%mod;
    }
};
