#define ll long long
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int M=1e9+7, n=nums.size();
        vector<vector<ll>> dp(n, vector<ll>(51, 0));
        for(int i=0;i<=nums[0];i++) dp[0][i]=1;
        for(int i=1;i<n;i++) for(int x=0;x<=nums[i];x++) for(int p=0;p<=x;p++)
        if(nums[i-1]-p>=nums[i]-x) dp[i][x]=(dp[i][x]+dp[i-1][p])%M;
        ll res=0;
        for(ll i: dp.back()) res=(res+i)%M;
        return res;
    }
};
