class Solution {
    int MOD = 1000000007;
    int solve(vector<int>& nums , int idx , int prev1 , int prev2 , vector<vector<vector<int>>>& dp){
        if(idx == nums.size())
            return 1;

        if(dp[idx][prev1][prev2] != -1){
            return dp[idx][prev1][prev2];
        }

        int sum = 0;
        for(int i=prev1 ; i<=nums[idx] ; i++){
            if(nums[idx] - i <= prev2){
                sum = (sum%MOD + (solve(nums , idx+1 , i , nums[idx]-i , dp))%MOD)%MOD;
            }
        }

        //cout << idx << " " << prev1 << " " << prev2 << " " << sum << endl;
        return dp[idx][prev1][prev2] = sum;
    }
public:
    int countOfPairs(vector<int>& nums) {
        // curr value is defined by the prev value and the idx
        vector<vector<vector<int>>> dp(nums.size() , vector<vector<int>>(51 , vector<int>(51 , -1)));
        return solve(nums , 0 , 0 , 50 , dp);
    }
};
