class Solution {
public:
    int MOD = 1e9+7;

    int helper(int idx, int n, vector<int>& nums, int a1, int a2, vector<vector<vector<int>>>& dp) {
        if (idx == n) return 1;
        if (dp[idx][a1][a2] != -1) return dp[idx][a1][a2];

        int a = 0;
        for (int i = a1; i <= nums[idx]; i++) {
            int val = nums[idx] - i;
            if (val > a2) continue;
            a += helper(idx + 1, n, nums, i, val, dp);
            a %= MOD;
        }
        return dp[idx][a1][a2] = a;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return helper(0, n, nums, 0, 50, dp);
    }
};
