class Solution {
    int mod=1e9+7;
    int dp[2001][51][51];
    int solve(int idx,int prev1,int prev2,vector<int>&nums) {
        if(idx==nums.size())return 1;
        if(dp[idx][prev1][prev2]!=-1)return dp[idx][prev1][prev2];
        int ans=0;
        //cout<<ans<<endl;
        for(int i=prev1;i<=nums[idx];i++){
            if(nums[idx]-i<=prev2){
                ans=(ans+solve(idx+1,i,nums[idx]-i,nums))%mod;
            }
        }
        // for(auto it:nums){
        //     cout<<it<<endl;
        // }
        return dp[idx][prev1][prev2]=ans;
    }

public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        memset(dp,-1,sizeof(dp));
        return solve(0,0,nums[0],nums);
    }
};
