class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<long long> v(51, 0);
        long long modulo = 1e9 + 7;
        for (int j = 0; j <= nums[0]; j++) {
            v[j]++;
        }
        for (int i = 1; i < nums.size(); i++) {
            vector<long long> temp(51, 0);
            for (int j = 0; j <= nums[i]; j++) {
                for (int k = 0; k <= min(nums[i - 1], j); k++) {
                    if (nums[i - 1] - k >= nums[i] - j) {
                        temp[j] += v[k];
                        temp[j] %= modulo;
                    }
                }
            }
            v = temp;
        }
        long long res = 0;
        for (int i = 0; i < 51; i++) {
            res = (res + v[i]) % modulo;
        }
        return res;
    }
};
