class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        const int MAX_NUM = 50;
        int n = nums.size();

        // DP table definition
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(MAX_NUM + 1, vector<int>(MAX_NUM + 1, 0)));

        // Initialize DP table for the first element
        for (int x = 0; x <= nums[0]; ++x) {
            int y = nums[0] - x;
            if (x >= 0 && y >= 0 && x <= MAX_NUM && y <= MAX_NUM) {
                dp[0][x][y] = 1;
            }
        }

        // Fill the DP table
        for (int i = 1; i < n; ++i) {
            for (int x = 0; x <= nums[i]; ++x) {
                int y = nums[i] - x;
                if (x >= 0 && y >= 0 && x <= MAX_NUM && y <= MAX_NUM) {
                    for (int prevX = 0; prevX <= nums[i-1]; ++prevX) {
                        int prevY = nums[i-1] - prevX;
                        if (prevX >= 0 && prevY >= 0 && prevX <= MAX_NUM && prevY <= MAX_NUM) {
                            if (x >= prevX && y <= prevY) { // Check monotonic conditions
                                dp[i][x][y] = (dp[i][x][y] + dp[i-1][prevX][prevY]) % MOD;
                            }
                        }
                    }
                }
            }
        }

        // Calculate the result
        int result = 0;
        for (int x = 0; x <= nums[n-1]; ++x) {
            int y = nums[n-1] - x;
            if (x >= 0 && y >= 0 && x <= MAX_NUM && y <= MAX_NUM) {
                result = (result + dp[n-1][x][y]) % MOD;
            }
        }

        return result;
    }
};
