class Solution {
public:
    int MOD = 1000000007;

    int solve(vector<int>& nums, int i, int incVal, int decVal, vector<vector<vector<long long>>>& dp) {
        if (i == nums.size()) return 1;
        if (dp[i][incVal][decVal] != -1) {
            return dp[i][incVal][decVal];
        }

        long long ans = 0;
        for (int a = incVal; a <= nums[i]; a++) {
            int b = nums[i] - a;
            if (b >= 0 && b <= decVal) {
                ans = (ans%MOD + solve(nums, i+1, a, b, dp)%MOD)%MOD;
            }

        }

        return dp[i][incVal][decVal] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<long long>>> dp(n, vector<vector<long long>> (51, vector<long long> (51, -1)));

        return solve(nums, 0, 0, nums[0], dp);
    }
};
