class Solution {
public:
const int M=1000000007;
int rec(int idx,int preva,int prevb,const int &n,vector<int>&nums,vector<vector<vector<int>>>&dp)
{
    if(idx==n)
    {
        return 1;
    }
    if(nums[idx]<preva)
    {
        return 0;
    }

    if(dp[idx][preva][prevb]!=-1)
    {
        return dp[idx][preva][prevb];
    }

    int ans=0;
    for(int i=preva;i<=nums[idx];i++)
    {
        int rem=nums[idx]-i;
        if(rem<=prevb)
        {
            ans=(ans%M+rec(idx+1,i,rem,n,nums,dp)%M)%M;
        }
    }
    return dp[idx][preva][prevb]=ans;
}
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>>dp(n+1,vector<vector<int>>(100,vector<int>(100,-1)));
        int ans=rec(0,0,70,n,nums,dp);
        return ans;
    }
};
