#define ll long long int
const int MOD = 1e9+7;

class Solution {
public:
    int countOfPairs(vector<int>& values) {
        size = values.size();
        dp = vector<vector<vector<ll>>>(size, vector<vector<ll>>(51, vector<ll>(51, -1)));
        return f(0, 0, values[0], values);
    }

private:
    int size;
    vector<vector<vector<ll>>> dp;

    ll f(int index, int val1, int val2, const vector<int>& values) {
        if (index >= size) return 1;
        if (dp[index][val1][val2] != -1) return dp[index][val1][val2];

        ll result = 0;
        for (int i = val1; i <= values[index]; i++) {
            for (int j = val2; j >= 0; j--) {
                if (i + j == values[index]) {
                    result = (result + f(index + 1, i, j, values)) % MOD;
                }
            }
        }

        return dp[index][val1][val2] = result;
    }
};
