class Solution {
public:
    int  mod=1e9+7;
    int helper(vector<int>&nums,int i,int prev1,int prev2,vector<vector<vector<int>>>& dp){
        if(i==nums.size()){
            return 1;
        }
        if(dp[i][prev1][prev2] != -1){
            return dp[i][prev1][prev2];
        }
        long long ways=0;
        if(i==0){
            for(int j=0;j<=nums[i];j++){
                ways+=helper(nums,i+1,j,nums[i]-j,dp);

            }
        }
        else{
            for(int j=0;j<=nums[i];j++){
                if(j>=prev1 and (nums[i]-j)<=prev2){
                    ways+=helper(nums,i+1,j,nums[i]-j,dp);
                }
            }
        }

        return dp[i][prev1][prev2]=ways%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>> dp(n+1,vector<vector<int>>(51,vector<int>(51,-1)));
        return helper(nums,0,0,0,dp);

    }
};
