#define MOD 1000000007

int countOfPairs(int* nums, int n) {
    int dp[n][51];
    memset(dp, 0, sizeof(dp));  

    for (int x = 0; x <= nums[0]; x++) {
        dp[0][x] = 1; 
    }

    for (int i = 1; i < n; i++) {
        int dpNext[51] = {0};  
        for (int x = 0; x <= nums[i]; x++) {
            for (int y = 0; y <= x; y++) { 
                int arr2_i = nums[i] - x;
                int arr2_y = nums[i-1] - y;
                if (arr2_y >= arr2_i) {  
                    dpNext[x] = (dpNext[x] + dp[i-1][y]) % MOD;
                }
            }
        }
        for (int x = 0; x <= nums[i]; x++) {
            dp[i][x] = dpNext[x];
        }
    }
    int result = 0;
    for (int x = 0; x <= nums[n-1]; x++) {
        result = (result + dp[n-1][x]) % MOD;
    }

    return result;
}
