class Solution {
public:

    int dp[2001][51][51];
    int MOD = 1e9 + 7;

    int helper(vector<int> &a, int i, int l1, int l2) {
        if(a.size() == i) return 1;

        if(dp[i][l1][l2] != -1) return dp[i][l1][l2];

        int res = 0;

        for(int j = 0; j <= a[i]; j++) {
            if(j < l1 || (a[i] - j) > l2) continue;
            res += helper(a, i+1, j, a[i] - j);
            res %= MOD;
        }

        return  dp[i][l1][l2] = res;
    }

    int countOfPairs(vector<int>& a) {
        memset(dp, -1, sizeof(dp));
        return helper(a, 0, 0, 50);
    }
};
