class Solution {
public:
    const int md = 1e9 + 7;
    vector<int> nums;
    unordered_map<int, unordered_map<int, unordered_map<int, int>>> dp;

    int rec(int i, int p1, int p2) {
        if (i == nums.size()) return 1;
        if (dp.count(i) && dp[i].count(p1) && dp[i][p1].count(p2))return dp[i][p1][p2];
        int res = 0;
        int maxi = nums[i];
        for (int x = 0; x <= maxi; ++x) {
            int y = maxi - x;
            if (x >= p1 && y <= p2) {
                res = (res + rec(i + 1, x, y)) % md;
            }
        }
        return dp[i][p1][p2] = res;
    }

    int countOfPairs(vector<int>& nums) {
        this->nums = nums;
        return rec(0, 0, 50);
    }
};
