class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        modulo = 1000000007
        ans_dct = {}
        def go(idx, a, b):
            if idx == len(nums):
                return 1
            if (idx, a, b) in ans_dct:
                return ans_dct[(idx, a, b)]
            val = nums[idx]
            if a > val:
                return 0
            min_a = max(a,val-b)
            tot = 0
            for i in range(min_a, val+1):
                if val-i <= b:
                    tot += go(idx+1, i, val-i)
            ans_dct[(idx, a, b)] = tot
            return tot
        count = 0    
        for i in range(nums[0]+1):
            count += go(1, i, nums[0]-i)
        return count % modulo
