class Solution {
public:

int dp[2010][55][55];

int helper(int x, int y, int i, vector<int>&nums)
{
  if(i>=nums.size()) return 1;

  if(dp[i][x+1][y+1] !=  -1 ) return dp[i][x+1][y+1];

   int res = 0;

    for(int j = 0;j<=nums[i];++j)
    {
      if((x==-1 && y==-1) || (j>=x && y >= nums[i] - j))
      {
         res = (res + helper(j, nums[i] - j, i+1, nums)) % 1000000007;
      }

    }

  return dp[i][x+1][y+1] = res;
}
    int countOfPairs(vector<int>& nums) {
      memset(dp, -1, sizeof(dp));  
      return helper(-1,-1,0,nums);
    }
};
