class Solution {
public:
  const int mod = 1e9+7;
   int func(int ind,int prevd,int previ,vector<int> &nums,vector<vector<vector<int>>> &dp){
      int n = nums.size();

      if(ind == n) return 1;

      if(dp[ind][prevd+1][previ+1] != -1) return dp[ind][prevd+1][previ+1];
      int ans = 0;
      for(int i = 0; i <= nums[ind]; i++){
        int temp = 0;
        if(prevd == -1 && previ == -1){
            temp = func(ind+1,i,nums[ind]-i,nums,dp);
        }
        else if(prevd >= i && nums[ind]-i >= previ){
            temp = func(ind+1,i,nums[ind]-i,nums,dp);
        }
        ans += temp;
        ans %= mod;
      }

      return dp[ind][prevd+1][previ+1] = ans;
   }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        vector<vector<vector<int> > > dp(
        n+1, vector<vector<int> >(55, vector<int>(55,-1)));
        int ans = func(0,-1,-1,nums,dp);
        return ans;
    }
};
