class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n=a.size(),m=50;
        vector<vector<int>> dp(n,vector<int> (m+1,0));
        for(int i=0;i<=min(m,a[0]);i++)dp[0][i]=1;
        // for(int i=0;i<=m;i++)cout << dp[0][i] << " ";cout << endl;
        for(int i=1;i<n;i++){
            for(int j=0;j<=m;j++){
                for(int k=0;k<=m;k++){
                    if(j<=k && a[i-1]-j>=a[i]-k && min({j,k,a[i-1]-j,a[i]-k})>=0)dp[i][k]=(dp[i][k]+dp[i-1][j])%1000000007;
                    // if(i<n-1)continue;
                    // if(j<=k && a[i-1]-j>=a[i]-k && min({j,k,a[i-1]-j,a[i]-k})>=0)cout << j << " " << k << " " << dp[i][j] << " " << dp[i-1][k] << endl;
                }
                // cout << dp[i][j] << " ";
            }
            // cout << endl;
        }
        int ans=0;
        for(int i=0;i<=m;i++)ans=(ans+dp[n-1][i])%1000000007;
        return ans;
    }
};
