class Solution {
public:
    typedef long long ll;
    long long mod = 1e9 + 7;
    ll solve(int i,int prev,vector<int>& nums,vector<vector<int>> &dp){
        if(i==nums.size())return 1;

        if(dp[i][prev] != -1)return dp[i][prev];
        int u = prev;
        int v;
        if(i-1>=0)v = nums[i-1]-u;
        else v= nums[i];
        ll ans = 0;

        for(int j=prev;j<=nums[i];j++){
            if(nums[i]-j <= v){
                ans+=solve(i+1,j,nums,dp);
                ans = ans%(mod);
            }
        }
        return dp[i][prev] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n,vector<int>(52,-1));
        return solve(0,0,nums,dp);
    }
};
