#define MOD ((int)1e9 + 7)
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<vector<unordered_map<int, int>>> memo(nums.size(), vector<unordered_map<int, int>>(60));
        return dp(0, nums[0], 0, nums, memo);
    }

    int dp(int l, int t, int i, vector<int>& nums, vector<vector<unordered_map<int, int>>> &memo) {
        if (i == nums.size()) return 1;
        if (memo[i][l].count(t)) return memo[i][l][t];

        int &ans = memo[i][l][t] = 0;
        for (int ll = l; ll <= nums[i]; ll++) {
            if ((nums[i] - ll) > t) continue;
            ans += dp(ll, nums[i] - ll, i + 1, nums, memo);
            ans %= MOD;
        }

        return ans;
    }
};
