class Solution {
public:
    int n;
    const int mod=1e9+7;
    int dp[2001][52][52];
    int solve(int i,int mini,int maxi,vector<int> &nums)
    {
        if(i==n) return 1;
        if(dp[i][mini][maxi]!=-1) return dp[i][mini][maxi];
        int ans=0;
        for(int j=mini;j<=nums[i];j++)
        {
            int arr1=j;
            int arr2=nums[i]-j;
            if(arr1>=mini&&arr2<=maxi)
            {
                ans=(ans+solve(i+1,arr1,arr2,nums))%mod;
            }
        }
        return dp[i][mini][maxi]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        memset(dp,-1,sizeof(dp));
        return solve(0,0,51,nums);
    }
};
