    int f(int[] ar , int i , int pre1 ,int pre2 , Integer[][][] dp){
        if(i >= ar.length) return 1;
        if(dp[i][pre1][pre2] != null ) return dp[i][pre1][pre2];

        long total =0;
        int n = ar[i];
        int j=0;
        for(j=0;j<=n;j++){
            int a = j;
            int b = n-j;

            if(a >= pre1 && b <= pre2){
                total = (total+ f(ar , i+1 , a , b , dp))%mod;
            }
        }

        return dp[i][pre1][pre2] = (int)total;

    }
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        Integer[][][] dp = new Integer[n][51][51];
        return f(nums , 0 , 0 , 50 , dp);
    }
}
