class Solution {
    long long mod = 1e9+7;
    int n;
public:
    int countOfPairs(vector<int>& nums) {
        long long ans = 0;
        n = nums.size();

        vector<vector<vector<long long>>> dp(n, vector<vector<long long>>(51, vector<long long>(51, 0)));
        for(int i = 0; i <= nums[0]; i++) {
            dp[0][i][nums[0]-i] = 1;
        }

        for(int i = 1; i < n; i++) {
            for(int j = 0; j <= nums[i]; j++) {
                for(int k = 0; k <= nums[i-1]; k++) {
                    if(j >= k && nums[i-1]-k >= nums[i]-j) {
                        dp[i][j][nums[i]-j] += dp[i-1][k][nums[i-1]-k];
                        dp[i][j][nums[i]-j] %= mod;
                    }
                }
            }
        }

        for(int i = 0; i <= nums[n-1]; i++) {
            ans = (ans + dp[n-1][i][nums[n-1]-i]) % mod;
        }

        return (int) ans;
    }
};
