typedef long long int ll;
ll mod = 1e9+7;
class Solution {
    private:
        ll helper(int index,vector<int> &nums, int prev1,int prev2,vector<vector<vector<ll>>> &dp){
            int n=nums.size();
            if(index==n)return (ll)1;
            if(dp[index][prev1+1][prev2+1]!=-1)return dp[index][prev1+1][prev2+1];
            ll ans = 0;
            if(index<n-1 && nums[index]>=nums[index+1]){
                for(int nextNo = 0;nextNo<=nums[index+1]; nextNo++){
                    if((prev1==-1 && prev2==-1) || (prev1<=nextNo && prev2>=nums[index]-nextNo)){
                        ans=(ans+helper(index+1,nums,nextNo,nums[index]-nextNo,dp))%mod;
                    }
                }
            }
            else{
                for(int nextNo = 0;nextNo<=nums[index]; nextNo++){
                    if((prev1==-1 && prev2==-1) || (prev1<=nextNo && prev2>=nums[index]-nextNo)){
                        ans=(ans+helper(index+1,nums,nextNo,nums[index]-nextNo,dp))%mod;
                    }
                }
            }
            return dp[index][prev1+1][prev2+1] = ans;
        }
public:
    ll countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<ll>>> dp(n+1,vector<vector<ll>>(52,vector<ll>(52,-1)));
        ll ans = helper(0,nums,-1,-1,dp);
        return ans%mod;
    }
};
