class Solution:
    MOD = 1000000007

    def init(self):
        self.dp = []

    def find_pairs(self, justiceLeague, index, superman, wonderWoman):
        if index >= len(justiceLeague):
            return 1

        if self.dp[index][superman][wonderWoman] != -1:
            return self.dp[index][superman][wonderWoman]

        total = 0
        for batman in range(justiceLeague[index] + 1):
            aquaman = batman
            flash = justiceLeague[index] - batman
            if aquaman >= superman and flash <= wonderWoman:
                total = (total + self.find_pairs(justiceLeague, index + 1, aquaman, flash)) % Solution.MOD

        self.dp[index][superman][wonderWoman] = total
        return total

    def countOfPairs(self, justiceLeague):
        n = len(justiceLeague)
        self.dp = [[[-1] * 51 for _ in range(51)] for _ in range(n)]
        return self.find_pairs(justiceLeague, 0, 0, 50)
