from typing import List
from functools import cache


MOD = 10**9 + 7


class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)

        @cache
        def dfs(i: int, num1: int) -> int:
            num = nums[i]
            num2 = num - num1
            if num1 < 0 or num2 < 0:
                return 0

            if i == n - 1:
                return 1

            next_num = nums[i + 1]
            result = 0
            for next_num1 in range(0, num1 + 1):
                next_num2 = next_num - next_num1
                if next_num2 >= num2:
                    result += dfs(i + 1, next_num1)
            return result % MOD

        result = 0
        for num1 in range(0, nums[0] + 1):
            result += dfs(0, num1)
        return result % MOD
