#define ll long long
ll mod = 1e9+7;
class Solution {
public:
    long long f(int i,int j1,int j2,vector<int> &nums,vector<vector<vector<int>>> &dp,int &n,int &maxi){
        if(i >= n) return 1; 
        if(dp[i][j1][j2] != -1) return dp[i][j1][j2]; 
        long long ans = 0; 
        for(int i1 = j1; i1 <= maxi ; i1++){
            for(int i2 = j2; i2 >= 0; i2--){
                if(i1 + i2 == nums[i]){
                    ans += f(i+1,i1,i2,nums,dp,n,maxi); 
                    ans %= mod; 
                }else if(i1+i2 < nums[i])break; 
            }
        }
        return dp[i][j1][j2] = ans%mod; 
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size(); 
        int maxi = *max_element(nums.begin(),nums.end()); 
        vector<vector<vector<int>>> dp(n,vector<vector<int>>(maxi+1,vector<int>(maxi+1,-1))); 
        return f(0,0,maxi,nums,dp,n,maxi); 
    }
};
